// export default Revenue
const Revenue = () => {
  return (
    <section id="revenue" className="revenue">
      <div className="container" data-aos="fade-up">
        <div className="section-header">
          <h2>How We Help You</h2>
        </div>

        <div className="row gy-5">
          <div className="col-lg-6" data-aos="fade-up" data-aos-delay="100">
            <div className="service-item">
              <h3>All-in-One Financial Support</h3>
              <p>
                We believe in taking care of everything - from keeping your
                books straight to helping you plan for taxes, guiding your
                investments, and understanding the stock market. It's like
                having a financial buddy who's got your back.
              </p>
            </div>

            <div className="service-item">
              <h3>Friendly Financial Advice</h3>
              <p>
                Our team is made up of experts in money matters. We chat with
                you regularly to share useful tips, let you know about changes
                in money rules, and show you ways to make your money work
                better for you.
              </p>
            </div>

            <div className="service-item">
              <h3>Learning Together</h3>
              <p>
                <strong>Understanding Money Talk:</strong> We know money can
                be confusing. That's why we're on a mission to help you
                understand it better, whether it's about taxes, investments, or
                the stock market. We want you to feel confident and in control
                of your finances.
              </p>
              <p>
                <strong>Keeping It Simple:</strong> We believe in clear and
                open communication. No jargon, just plain English. We're here
                to answer your questions, address concerns, and make sure
                you're comfortable every step of the way.
              </p>
            </div>
          </div>

          <div className="col-lg-6" data-aos="fade-up" data-aos-delay="200">
            <div className="service-item">
              <h3>Smart Tech to Make Things Easy</h3>
              <p>
                <strong>Tools That Work for You:</strong> We use smart
                technology to make things easy for you. From tracking your
                spending to helping you with taxes and understanding stock
                market trends, our tools are designed to be user-friendly and
                reduce any chances of mistakes.
              </p>
              <p>
                <strong>Digital Tax Help:</strong> Filing taxes can be a
                headache. But with our online tools, it becomes a breeze. We
                make sure you're following all the tax rules without any
                stress.
              </p>
            </div>

            <div className="service-item">
              <h3>Making Smart Money Moves</h3>
              <p>
                <strong>Balancing Risks:</strong> We help you plan your
                investments wisely, making sure you don't put all your eggs in
                one basket. It's about finding the right balance to keep your
                money safe and growing.
              </p>
              <p>
                <strong>Knowing Your Comfort Zone:</strong> We figure out how
                much risk you're comfortable with, not just in regular
                investments but also in the stock market. Your investments
                should be like a comfy pair of shoes - just the right fit.
                It's all about making you feel good about your money moves.
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Revenue;
